INSERT INTO `edu_stage` (`id`, `category_id`, `title`, `description`, `stage`, `is_deleted`, `gmt_create`, `gmt_modified`) VALUES (4,4,'Java基础','Java基础知识','1',0,'2021-11-15 21:33:20','2021-11-15 21:33:20'),(5,4,'Java高级','Java进阶技能','2',9,'2021-11-16 20:24:43','2021-11-16 20:24:44'),(6,4,'Java服务','微服务','3',0,'2021-11-17 19:34:58','2021-11-17 19:34:58'),(7,4,'数据库设计','java数据库','4',0,'2021-11-17 19:39:56','2021-11-17 19:39:56'),(8,4,'Java无敌','无敌','5',0,'2021-11-17 19:41:30','2021-11-17 19:41:30'),(9,19,'前端入门','本阶段对HTML5,CSS3等内容重点讲解，且穿插了很多练习案例，在掌握前端基础核心语法以外会再进行综合性练习并且会针对性讲解动画，过渡，及多种经典布局方案，对JavaScript入门到高级进行全面讲解，每个知识点都有相应的案例练习，且针对闭包，原型，继承等面向对象高级语法进行深入讲解，让学员能够很好的扎实基础，为后续的框架学习做好充足的准备','2',0,'2021-11-28 17:42:58','2021-11-28 17:42:58'),(10,19,'ES6+Node+工程化','本阶段开发接触前端高级语法，如ES6新特性，git及git高级等，且会涉及服务器端知识及工程化开发工具使用，使得学生对工程化开发有了全面的认识，并对前后端通信有了深入的了解','3',0,'2021-11-28 17:44:41','2021-11-28 17:44:41'),(11,19,'前端框架','本阶段主要针对前端高级框架React/Vue/Uni-app/微信小程序等框架进行全面讲解','4',0,'2021-11-28 17:51:58','2021-11-28 17:51:58'),(12,19,'项目实战','本阶段为学生毕业前的综合实战练习，主要培养学生独立动手能力，且对前端所有所学重点知识进行综合性复习讲解，使得学生从容应对面试，快速走上工作岗位','5',0,'2021-11-28 18:10:29','2021-11-28 18:10:29'),(13,19,'计算机基础','深入底层原理的关键技能','1',0,'2021-11-28 17:28:58','2021-11-28 17:28:58'),(15,18,'编程基础','为了理解计算机底层的运行原理。即便写上层应用代码的程序员，工作中遇到的很多问题是与底层机制相关的，如果不学底层这些知识，就会在遇到问题的时候束手无策','1',0,'2021-12-02 16:58:16','2021-12-02 16:58:16'),(16,18,'Java核心技术','本阶段除了JavaSE中要求大家必会的java基础知识外，重点加强了数据结构思想、集合源码分析、jdk9-11新特性的介绍，让学员不光“知其然，还要知其所以然”，从最开始便培养学员深入学习和架构思维能力。','2',0,'2021-12-02 16:59:48','2021-12-02 16:59:48'),(17,18,'Web网页技术','本阶段以项目案例为驱动，采用所学即所用的方式指导大家学习，在边学边练过程中，可深入掌握Web开发技术，具备与之匹配的实战能力','3',0,'2021-12-02 17:00:46','2021-12-02 17:00:46'),(18,18,'开发必备框架&技术','SSM框架的学习和框架设计，入门门槛都太高，理解起来晦涩。课程抓住这两个痛点。 通过从0搭建一个较为完备的web框架来提升自己的开发能力，同时能够管中窥豹，去了解SSM框架的设计思路，再辅以通俗易懂的Spring和mybatis核心模块源码的讲解，将课程门槛拉低， 让大家通过学习本门课程后能够通过自己学习读懂Spring乃至其他框架的源码。 本课程的目标是：让新手学到技术，让老手学到思想，让高手学到境界。','4',0,'2021-12-02 17:03:20','2021-12-02 17:03:20'),(19,18,'项目实战','本课程由大厂技术大咖与尚硅谷组成专家团队精心打磨，并定期更新，囊括企业面试和开发中主流前沿的技术，涵盖Java开发工程师不同成长阶段的问题及优选解决方案。每个热门技术当做单课深入讲解，而非某些机构只是一带而过，或项目中简单使用。 相信只有让学员可以系统学习和掌握核心技术，才能在面试和开发中脱颖而出','5',0,'2021-12-02 17:04:45','2021-12-02 17:04:45'),(20,20,'Linux基础','主要学习Linux系统，生产环境中基本都在字符界面完成工作，所以要掌握常用的几十个基本管理命令，包括用户管理、磁盘分区、软件包管理、文件权限、文本处理、进程管理、性能分析工具等。','1',0,'2021-12-02 19:15:57','2021-12-02 19:15:57'),(21,20,'系统管理','掌握常用的几十个基本管理命令，包括用户管理、磁盘分区、软件包管理、文件权限、文本处理、进程管理、性能分析工具等。','2',0,'2021-12-02 19:16:42','2021-12-02 19:16:42'),(23,20,'网络基础','OSI和TCP/IP模型一定要熟悉。基本的交换机、路由器概念及实现原理要知道。','3',0,'2021-12-02 19:18:06','2021-12-02 19:18:06'),(24,20,'Shell脚本编程基础','掌握Shell基本语法结构，能编写简单的脚本即可','4',0,'2021-12-02 19:19:19','2021-12-02 19:19:19'),(25,20,'网络服务','最常用的网络服务一定得会部署，比如vsftp、nfs、samba、bind、dhcp等','5',0,'2021-12-02 19:20:16','2021-12-02 19:20:16');
